CREATE TABLE "users" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE, "name" VARCHAR, "password" VARCHAR, "defaultAccess" INTEGER, "knx" INTEGER, "modified" DATETIME DEFAULT CURRENT_TIMESTAMP);
